package sec02;

public class SwitchExam {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int num = (int) (Math.random()*+1);
		switch(num) {
	    case 8:
			System.out.println("8시이면 출근을합니다.");
		case 9:
		System.out.println("9시이면 회의을합니다.");
		case 10:
			System.out.println("10시이면 업무를 시작합니다.");
		case 11:
		System.out.println("11시이면 출근을합니다.");
		
		}
			
	}

}
